def partion(n,r):
    if n!=0:
        total=n
        temp = []
        for i in range(r):
            val = np.random.randint(0, total)
            temp.append(val)
            total -= val
        if total!=0: temp[-1]+=total
        random.shuffle(temp)
        assert sum(temp) == n
        return temp
    else:
        return [0]*r


