def partion(n,r):
    normal_pop=list(np.random.normal(n/r,n/r/4,size=r))
    pop=[round(i) for i in normal_pop]
    dif=n-sum(pop)
    add=int(dif/r)
    pop=[i+add for i in pop]
    index=np.random.randint(0, r)
    dif=n-sum(pop)
    pop[index]=pop[index]+dif
    
    return pop


